import { useState } from "react"
import PersonalBill from "./PersonalBill"
import styles from "./GroupBill.module.css"
import SimpleTabs from "./Tabs"

const initialState = [
    {   
        id: 1,
        name: "Joel",
        items: [],
        subtotal: 0,
        tax: 0,
        tip: 0,
        total: 0
    },
    {   
        id: 2,
        name: "Nohely",
        items: [],
        subtotal: 0,
        tax: 0,
        tip: 0,
        total: 0,
    }
]

const GroupBill = () => {
    const [people, setPeople] = useState(initialState)
    const [taxRate, setTaxRate] = useState(0.08875)
    const [tipRate, setTipRate] = useState(.18)

    const addItemToPerson = (name, price, personId) => {
        let currentPerson = people.filter(person => person.id === personId)
        currentPerson[0]['items'].push({
            name: name,
            price: price,
            qty: 1
        })
        currentPerson[0]['subtotal'] += parseFloat(parseInt(price))
        currentPerson[0]['tax'] = Math.round(100*(currentPerson[0]['subtotal']) * taxRate)/100
        currentPerson[0]['tip'] = Math.round(100*(currentPerson[0]['subtotal']) * tipRate)/100
        currentPerson[0]['total'] = Math.round(100*(currentPerson[0]['subtotal'] + currentPerson[0]['tax'] + currentPerson[0]['tip']))/100
        setPeople(prevState => {
            return [...prevState, currentPerson]
        })
    }

    const incrementItemQuantity = (item, personId) => {
        let currentPerson = people.filter(person => person.id === personId)
        currentPerson[0]['items']

    }

    const decrementItemQuantity = () => {
        console.log('updating item down') 
     }

    return (
        <>
            <div style={{textAlign: 'center'}}>
                <img src='./split_logo.png' alt='logo' style={{width: '50%'}}/>
            </div>
            <SimpleTabs 
                people={people} 
                addItemToPerson={addItemToPerson} 
                incrementItemQuantity={incrementItemQuantity}
                decrementItemQuantity={decrementItemQuantity}
            />
        </>
    )
}

export default GroupBill
