import { useState } from "react"
import PersonalBill from "./PersonalBill"
import styles from "./GroupBill.module.css"

const initialState = [
    {
        name: "Joel",
        items: [
            { 
                name: "burger",
                qty: 1,
                price: 20
            }
        ]
    },
    {
        name: "Nohely",
        items: [
            { 
                name: "empanadas",
                qty: 1,
                price: 20
            }
        ]
    }
]

const GroupBill = () => {
    const [people, setPeople] = useState(initialState)

    return (
        <>
            <div style={{textAlign: 'center'}}>
                <img src='./split_logo.png' alt='logo' style={{width: '50%'}}/>
            </div>
            <div className={styles.groupBillContainer}>
                {people.map(person => (
                    <PersonalBill person={person} />
                ))}
            </div>
        </>
    )
}

export default GroupBill
