import { useState } from "react"
import PersonalBill from "./PersonalBill"
import styles from "./GroupBill.module.css"
import SimpleTabs from "./Tabs"

const initialState = [
    {   
        id: 1,
        name: "Joel",
        items: [
            { 
                name: "burger",
                qty: 1,
                price: 20
            }
        ],
        total: 0
    },
    {   
        id: 2,
        name: "Nohely",
        items: [
            { 
                name: "empanadas",
                qty: 1,
                price: 20
            }
        ]
    }
]

const GroupBill = () => {
    const [people, setPeople] = useState(initialState)

    const addItemToPerson = (name, price, personId) => {
        let currentPerson = people.findIndex(person => person.id === personId)
        setPeople(prevState => {
            let currentPeople = [...prevState]
            currentPeople[currentPerson]['items'].push({
                name: name,
                price: price,
                qty: 1
            })
            return currentPeople
        })
    }

    return (
        <>
            <div style={{textAlign: 'center'}}>
                <img src='./split_logo.png' alt='logo' style={{width: '50%'}}/>
            </div>
            <SimpleTabs people={people} addItemToPerson={addItemToPerson}/>
        </>
    )
}

export default GroupBill
